mapper.update:
UPDATE knowledge
SET tag = #{tag}
WHERE id = #{id}

UPDATE knowledge
SET is_delete = 1
WHERE id = #{kid};

mapper.select:
SELECT id, app_type, title, content, link, tag
FROM knowledge
WHERE creator = #{userId}

SELECT id, app_type, title, content, link
FROM knowledge
         JOIN knowledge_review_state krs on knowledge.id = krs.knowledge_id
WHERE creator = #{userId}
  AND next_review_time  <  #{deadline}
ORDER BY id DESC;

SELECT tag
from knowledge
where id = #{id}

SELECT k.id, k.app_type, k.title, krs.review_count, krs.current_interval, krs.next_review_time
FROM knowledge k
         JOIN knowledge_review_state krs ON k.id = krs.knowledge_id
WHERE k.creator = #{userId}
ORDER BY k.id DESC;

SELECT title, content, link, tag FROM knowledge
WHERE id IN

    #{item}

SELECT 1
FROM knowledge
WHERE id = #{kid}
  AND creator = #{userId}
