mapper.select:
SELECT id, name, creator
FROM tag
WHERE creator = #{creator}
  AND is_delete = 0;

SELECT id, name, creator
FROM tag
WHERE creator = #{creator}
  AND name = #{name}
  AND is_delete = 0;

SELECT id
FROM tag
WHERE (creator = #{creator} OR creator = 0)
AND name IN

    #{name}

AND is_delete = 0;

SELECT name
from tag
where id = #{tagId};

SELECT id, name
FROM tag
WHERE creator = 0;

mapper.update:
UPDATE tag
SET tag.is_delete = true
WHERE tag.id = #{id}
